def combien
puts "Il y a #{@journalists.count} journalistes dans cet array."
end

def methode
a = 0
@journalists.each do |variable|
	x = (variable.include?("9") or variable.include?("8") or variable.include?("7") or variable.include?("6") or variable.include?("5") or variable.include?("4") or variable.include?("3") or variable.include?("2") or variable.include?("1") or variable.include?("0"))
	if x
	a += 1
	end
end
puts "Il y a #{a} handles qui contiennent un numéro."
end

def methode2
a = 0
@journalists.each do |variable|
	x = (variable.include?("aude") or variable.include?("Aude"))
	if x
	a += 1
	end
end
puts "Il y en a #{a} qui contiennent le prénom Aude."
end

def methode3
a = 0
@journalists.each do |variable|
	if variable[1] == variable[1].upcase
	a += 1
	end
end
puts "Il y en a #{a} qui commencent par une majuscule."
end

def methode4
a = 0
@journalists.each do |variable|
	if variable =~ /[A-Z]/
	a += 1
	end
end
puts "#{a} handle contiennent des majuscules"
end

def methode5
a = 0
@journalists.each do |variable|
	if (variable.include? ('_'))
	a += 1
	end
end
puts "#{a} handle contiennent des _"
end

def methode6
puts @journalists.sort_by { |word| word.downcase }
end

def perfom
combien
methode
methode2
methode3
methode4
methode5
methode6
end

perfom
